"use client";

import { useState, useMemo } from "react";
import { ProjectCardFeatured } from "./ProjectCardFeatured";
import { ProjectCardCompact } from "./ProjectCardCompact";
import { ScrollReveal } from "@/components/interactive/ScrollReveal";
import type { Project } from "@/data/projects";

interface ProjectListProps {
  projects: Project[];
}

export function ProjectList({ projects }: ProjectListProps) {
  const [query, setQuery] = useState("");

  const filtered = useMemo(() => {
    if (!query.trim()) return projects;
    const q = query.toLowerCase();
    return projects.filter(
      (p) =>
        p.title.toLowerCase().includes(q) ||
        p.description.toLowerCase().includes(q) ||
        p.techStack.some((t) => t.toLowerCase().includes(q)) ||
        p.category.toLowerCase().includes(q)
    );
  }, [projects, query]);

  const featured = filtered.filter((p) => p.featured);
  const archive = filtered.filter((p) => !p.featured);

  return (
    <>
      <div className="mt-8">
        <div className="relative">
          <svg
            className="pointer-events-none absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-foreground-subtle"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            viewBox="0 0 24 24"
          >
            <circle cx="11" cy="11" r="8" />
            <path d="m21 21-4.3-4.3" strokeLinecap="round" />
          </svg>
          <input
            type="text"
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            placeholder="Search projects by name, tech, or category..."
            className="w-full rounded-lg border border-border bg-background-card py-2.5 pl-10 pr-4 text-sm text-foreground placeholder:text-foreground-subtle focus:border-accent/60 focus:outline-none focus:ring-1 focus:ring-accent/60 transition-colors"
          />
        </div>
      </div>

      {filtered.length === 0 && (
        <p className="mt-12 text-center text-sm text-foreground-muted">
          No projects match &ldquo;{query}&rdquo;
        </p>
      )}

      {featured.length > 0 && (
        <div className="mt-8 grid gap-6 md:grid-cols-2">
          {featured.map((project, index) => (
            <ScrollReveal key={project.slug} delay={index * 0.1} className="h-full">
              <ProjectCardFeatured
                title={project.title}
                description={project.description}
                techStack={project.techStack}
                links={project.links}
                slug={project.slug}
              />
            </ScrollReveal>
          ))}
        </div>
      )}

      {archive.length > 0 && (
        <>
          <ScrollReveal>
            <h2 className="mt-16 text-2xl font-semibold text-foreground">
              More Projects
            </h2>
          </ScrollReveal>
          <div className="mt-6 grid gap-4 sm:grid-cols-2 lg:grid-cols-3">
            {archive.map((project, index) => (
              <ScrollReveal key={project.slug} delay={index * 0.1} className="h-full">
                <ProjectCardCompact
                  title={project.title}
                  description={project.description}
                  techStack={project.techStack}
                  slug={project.slug}
                />
              </ScrollReveal>
            ))}
          </div>
        </>
      )}
    </>
  );
}

import Link from "next/link";
import { PageLayout } from "@/components/layout/PageLayout";
import { SectionContainer } from "@/components/ui/SectionContainer";
import { Card } from "@/components/ui/Card";
import { ProjectCardFeatured } from "@/components/sections/ProjectCardFeatured";
import { ScrollReveal } from "@/components/interactive/ScrollReveal";
import { Terminal } from "@/components/interactive/Terminal";

import { featuredProjects } from "@/data/projects";

const quickLinks = [
  {
    title: "Projects",
    description: "AI systems, full-stack apps, and research papers.",
    href: "/projects",
  },
  {
    title: "Golf",
    description: "D1 collegiate career, stats, and swing breakdown.",
    href: "/golf",
  },
  {
    title: "Blog",
    description: "Writing on AI, engineering, and competing.",
    href: "/blog",
  },
];

export default function Home() {
  const highlighted = featuredProjects.slice(0, 2);

  return (
    <PageLayout>
      {/* ── Hero Text ── */}
      <SectionContainer className="py-24">
        <ScrollReveal>
          <h1 className="text-5xl font-bold tracking-tight text-foreground sm:text-6xl">
            Cameron Keith
          </h1>
          <p className="mt-4 max-w-xl text-lg text-foreground-muted">
            D1 Golfer. AI Engineer. Building at the intersection.
          </p>
          <p className="mt-4 max-w-2xl text-base leading-relaxed text-foreground-muted">
            I am a Dartmouth student-athlete, AI researcher, and startup founder
            turning the discipline of competitive golf into engineering that
            matters.
          </p>
        </ScrollReveal>
      </SectionContainer>

      {/* ── Terminal ── */}
      <SectionContainer className="pb-24">
        <ScrollReveal>
          <Terminal />
        </ScrollReveal>
      </SectionContainer>

      {/* ── Quick-Link Cards ── */}
      <SectionContainer className="pb-24">
        <div className="grid gap-4 sm:grid-cols-3">
          {quickLinks.map((link, index) => (
            <ScrollReveal key={link.href} delay={index * 0.1}>
              <Link href={link.href} className="group block">
                <Card hover className="flex h-full flex-col justify-between">
                  <div>
                    <h3 className="text-lg font-semibold text-foreground">
                      {link.title}
                    </h3>
                    <p className="mt-1 text-sm text-foreground-muted">
                      {link.description}
                    </p>
                  </div>
                  <span className="mt-4 inline-block text-foreground-subtle transition-transform group-hover:translate-x-1">
                    &rarr;
                  </span>
                </Card>
              </Link>
            </ScrollReveal>
          ))}
        </div>
